import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{o as i,c as a,f as r}from"./app-370ca7c2.js";const l={},t=r('<h2 id="文本" tabindex="-1"><a class="header-anchor" href="#文本" aria-hidden="true">#</a> 文本</h2><p>font-size: 字体大小<br> color: 文字颜色<br> background-color：背景色<br> width：宽度<br> height：高度</p><h3 id="装饰线" tabindex="-1"><a class="header-anchor" href="#装饰线" aria-hidden="true">#</a> 装饰线</h3><p><mark>text-decoration ： 设置文字的装饰线</mark></p><ul><li>none：无任何装饰线(去除a元素的下划线)</li><li>underline：下划线</li><li>overline：上划线</li><li>line-through：中划线（删除线）</li></ul><h3 id="大小写转换" tabindex="-1"><a class="header-anchor" href="#大小写转换" aria-hidden="true">#</a> 大小写转换</h3><p>text-transform: 用于设置文字的大小写转换</p><ul><li>capitalize：(使…首字母大写, 资本化的意思)将每个单词的首字符变为大写</li><li>uppercase：(大写字母)将每个单词的所有字符变为大写</li><li>lowercase：(小写字母)将每个单词的所有字符变为小写</li><li>none：没有任何影响</li></ul><h3 id="缩进" tabindex="-1"><a class="header-anchor" href="#缩进" aria-hidden="true">#</a> 缩进</h3><p><mark>text-indent: 用于设置第一行内容的缩进</mark><br> text-indent: 2em; 刚好是缩进2个文字</p><h3 id="文本对齐" tabindex="-1"><a class="header-anchor" href="#文本对齐" aria-hidden="true">#</a> 文本对齐</h3><p>text-align： 设置文本对齐</p><ul><li>left：左对齐</li><li>right：右对齐</li><li>center：正中间显示</li><li>justify：两端对齐</li></ul><h3 id="字符间距" tabindex="-1"><a class="header-anchor" href="#字符间距" aria-hidden="true">#</a> 字符间距</h3><p>letter-spacing、word-spacing分别用于设置字母、单词之间的间距</p><ul><li>默认是0，可以设置为负数</li></ul>',16),n=[t];function h(d,c){return i(),a("div",null,n)}const u=e(l,[["render",h],["__file","02CSS文本.html.vue"]]);export{u as default};
