ssword = md5($password . $salt); // 组装注册的数据 $data = [ 'mobile' => $mobile, 'password' => $password, 'salt' => $salt ]; $result = $this->BusinessModel->validate('common/business/Business.register')->save($data); if ($result === false) { $this->error($this->BusinessModel->getError()); } else { $this->success('注册成功', url('/home/index/login')); } } return $this->fetch(); } \"]},\"606\":{\"h\":\"登录功能\"},\"607\":{\"h\":\"✒️登录流程图\",\"t\":[\"application\\\\home\\\\controller\\\\Index.php\",\" /** * 登录 */ public function login() { if ($this->request->isPost()) { $mobile = $this->request->param('mobile', '', 'trim'); $password = $this->request->param('password', '', 'trim'); $business = $this->BusinessModel->where(['mobile' => $mobile])->find(); if (!$business) { $this->error('用户不存在'); } if (md5($password . $business['salt']) != $business['password']) { $this->error('密码错误'); } $data = [ 'id' => $business['id'], 'mobile' => $business['mobile'] ]; cookie('LoginBusiness', $data); $this->success('登录成功', url('home/business/index')); } return $this->fetch(); } \",\"application\\\\home\\\\view\\\\business\\\\index.html 去除首页登录按钮\",\" <a href=\\\"login.html\\\" class=\\\"login wow fadeInUp\\\" data-wow-delay=\\\"200ms\\\">登录</a> \"]},\"608\":{\"h\":\"✨追加字段与加密手机号\",\"t\":[\"获取器 · ThinkPHP5.0完全开发手册 · 看云 (kancloud.cn)\",\"application\\\\common\\\\model\\\\business\\\\Business.php\",\" /** 追加字段 */ protected $append = [ 'mobile_text', 'avatar_cdn' ]; /** * 获取手机号将其加密 */ public function getMobileTextAttr($value, $data) { $mobile = $data['mobile'] ?? ''; if (empty($mobile)) { return false; } return substr_replace($mobile, '****', 3, 4); } \"]},\"609\":{\"h\":\"✨创建home公共控制器\",\"t\":[\"php think make:controller common/Home \",\"application\\\\common\\\\controller\\\\Home.php\",\"<?php namespace app\\\\common\\\\controller; use think\\\\Controller; use think\\\\Request; class Home extends Controller { /** 不需要登录的方法 */ protected $noNeedLogin = []; /** 全局登录的信息 */ protected $LoginBusiness = []; public function __construct() { parent::__construct(); // 获取当前的操作名 $action = $this->request->action(); // 判断哪些操作名需要调用验证登录 if (!in_array($action, $this->noNeedLogin) && !in_array('*', $this->noNeedLogin)) { $this->isLogin(); } } /** * 验证登录 */ protected function isLogin() { // 获取cookie $LoginBusiness = cookie('LoginBusiness') ?? []; if (empty($LoginBusiness)) { $this->error('请先登录', url('home/index/login')); } // 从登录信息获取相应的数据查询数据表有没有这个用户 $id = $LoginBusiness['id'] ?? 0; $mobile = $LoginBusiness['mobile'] ?? ''; // 查询数据表 $business = model('business.Business') ->where(['id' => $id, 'mobile' => $mobile]) ->find(); if (!$business) { // 清除非法cookie cookie('LoginBusiness', null); $this->error('非法登录', url('/home/index/login')); } // 查询出来的用户信息赋值全局使用 $this->LoginBusiness = $business; // 赋值全局的视图 $this->assign([ 'LoginBusiness' => $business ]); } } \",\"将Business的父类改为上面的Home\",\"application\\\\home\\\\controller\\\\Business.php\",\"use app\\\\common\\\\controller\\\\Home; class Business extends Home { public function index() { return $this->fetch(); } } \",\"登录后台管理面板 常规设置->系统设置 添加域名变量\",\" /** * 获取头像 */ public function getAvatarCdnAttr($value, $data) { $avatar = $data['avatar'] ?? ''; if (empty($avatar)) { $avatar = 'assets/home/images/avatar.jpg'; } // 获取网站域名 $cdn = config('site.url'); return $cdn . $avatar; } \",\"application\\\\home\\\\view\\\\business\\\\index.html修改img的路径\",\"<img src=\\\"{$LoginBusiness.avatar_cdn}\\\" /> \"]},\"610\":{\"h\":\"✨注销功能\",\"t\":[\"application\\\\home\\\\view\\\\business\\\\index.html 最底部添加\",\"<script> $('#logout').click(() => { mui.confirm( '是否确认退出', '退出提醒', ['确认','取消'], function(res) { if(res.index === 0) { $.ajax({ type:'post', url:`{:url('/home/index/logout')}`, dataType:'json', success:function(result) { if(result.code === 1) { mui.toast(result.msg); $('.mui-popup-backdrop').remove() $('.mui-popup').remove() setInterval(() => { location.href = `{:url('/home/index/login')}`; },2000); } } }); } } ) }) </script> \",\"application\\\\home\\\\controller\\\\Index.php\",\" /** * 注销 */ public function logout() { if ($this->request->isAjax()) { cookie('LoginBusiness', null); $this->success('退出账号成功'); } } \"]},\"611\":{\"h\":\"✨登录验证\",\"t\":[\"application\\\\home\\\\controller\\\\Index.php\",\" public function login() { // 判断是否已经登录 登录后重定向到首页 $LoginBusiness = cookie('LoginBusiness') ?? ''; if ($LoginBusiness) { $this->redirect(url('/home/business/index')); } // .... } public function register() { // 判断是否已经登录 登录后重定向到首页 $LoginBusiness = cookie('LoginBusiness') ?? ''; if ($LoginBusiness) { $this->redirect(url('/home/business/index')); } //.... } \"]},\"612\":{\"h\":\"🛠️代码重构优化\",\"t\":[\"减少重复代码：登录和注册方法中的判断是否已经登录的部分可以抽离一个单独的方法\",\"参数验证，在注册方法中，可以对手机号和密码进行非空验证\",\"注册成功后重定向首页抽离出函数：注册成功后，可以直接重定向登录页面\",\"使用密码哈希函数：可以使用密码哈希函数password_hash()来进行密码哈希和验证，而不是使用简单的md5。\",\"application\\\\home\\\\controller\\\\Index.php\",\"<?php namespace app\\\\home\\\\controller; use think\\\\Controller; class Index extends Controller { /** 用户模型 */ protected $BusinessModel = null; public function _initialize() { $this->BusinessModel = model('business.Business'); } public function index() { return $this->fetch(); } /** * 注册 */ public function register() { // 判断是否已经登录 登录后重定向到首页 if ($this->isUserLoggedIn()) { $this->redirectToHome(); } if ($this->request->isPost()) { $mobile = $this->request->param('mobile', '', 'trim'); $password = $this->request->param('password', '', 'trim'); // 验证参数 if (empty($mobile) || empty($password)) { $this->error('手机号和密码不能为空'); } // 生成密码盐和哈希密码 $salt = build_ranstr(); $hashedPassword = password_hash($password . $salt, PASSWORD_DEFAULT); // 组装注册的数据 $data = [ 'mobile' => $mobile, 'password' => $hashedPassword, 'salt' => $salt, 'money' => 0, 'auth' => 0, 'deal' => 0, ]; // 查询用户来源 $source = model('business.Source')->where(['name' => ['LIKE', '%云课堂%']])->find(); if ($source) { $data['sourceid'] = $source['id']; } // 保存用户数据 $result = $this->registerUser($data); if ($result === false) { $this->error($this->getRegisterError()); } else { $this->success('注册成功', url('/home/index/login')); } } return $this->fetch(); } /** * 登录 */ public function login() { // 判断是否已经登录 登录后重定向到首页 if ($this->isUserLoggedIn()) { $this->redirectToHome(); } if ($this->request->isPost()) { $mobile = $this->request->param('mobile', '', 'trim'); $password = $this->request->param('password', '', 'trim'); // 查询用户 $user = $this->getUserByMobile($mobile); if (!$user) { $this->error('用户不存在'); } // 验证密码 if (!password_verify($password . $user['salt'], $user['password'])) { $this->error('密码错误'); } // 设置登录信息 $data = [ 'id' => $user['id'], 'mobile' => $user['mobile'] ]; cookie('LoginBusiness', $data); $this->success('登录成功', url('home/business/index')); } return $this->fetch(); } /** * 判断是否已经登录 */ private function isUserLoggedIn() { $LoginBusiness = cookie('LoginBusiness') ?? ''; return !empty($LoginBusiness); } /** * 重定向到首页 */ private function redirectToHome() { $this->redirect(url('/home/business/index')); } /** * 注册用户 */ private function registerUser($data) { return $this->BusinessModel->validate('common/business/Business.register')->save($data); } /** * 获取注册错误信息 */ private function getRegisterError() { return $this->BusinessModel->getError(); } /** * 根据手机号获取用户 */ private function getUserByMobile($mobile) { return $this->BusinessModel->where(['mobile' => $mobile])->find(); } /** * 注销 */ public function logout() { if ($this->request->isAjax()) { cookie('LoginBusiness', null); $this->success('退出账号成功'); } } } \"]},\"613\":{\"h\":\"修改资料\"},\"614\":{\"h\":\"✨底部tabber选中切换颜色\",\"t\":[\"删除html中class属性mui-active 添加以下script\",\"application\\\\home\\\\view\\\\common\\\\footer.html\",\"<script> // 获取地址路径并且转为数组 let pathArr = location.pathname.split('/'); // 过滤数组里面的元素 pathArr = pathArr.filter(val => val && val.trim()) // 截取数组并且转成字符串 let path = pathArr.slice(0, 2).join('/'); // 截取数组并且转成字符串 if (path == '' || path == '/') { $('.footer-bar .tab-item').eq(0).addClass('mui-active') } else if (path == 'home/business') { $('.footer-bar .tab-item').eq(2).addClass('mui-active') } </script> \"]},\"615\":{\"h\":\"✨修改资料\",\"t\":[\"application\\\\home\\\\controller\\\\Business.php\",\" /** 用户模型 */ protected $BusinessModel = null; public function __construct() { parent::__construct(); $this->BusinessModel = model('business.Business'); } /** * 修改资料 */ public function profile() { return $this->fetch(); } \",\"application\\\\home\\\\view\\\\business\\\\index.html将基本资料a标签的href属性改为\",\"{:url('/home/business/profile') \",\"application\\\\home\\\\view\\\\business\\\\profile.html\",\"<link rel=\\\"stylesheet\\\" href=\\\"/assets/home/plugin/city/css/city-picker.css\\\"> <link rel=\\\"stylesheet\\\" href=\\\"/assets/home/css/profile.css\\\"> <div class=\\\"user\\\"> <div class=\\\"avatar wow fadeInUp\\\"> <img src=\\\"/assets/home/images/avatar.jpg\\\" /> </div> </div> <form class=\\\"mui-input-group\\\" method=\\\"post\\\" enctype=\\\"multipart/form-data\\\"> <div class=\\\"mui-input-row\\\"> <label>昵称</label> <input type=\\\"text\\\" name=\\\"nickname\\\" value=\\\"{$LoginBusiness.nickname}\\\" class=\\\"mui-input-clear\\\" placeholder=\\\"请输入昵称\\\"> </div> <div class=\\\"mui-input-row\\\"> <label>手机号码</label> <input type=\\\"text\\\" value=\\\"{$LoginBusiness.mobile}\\\" disabled class=\\\"mui-input-clear\\\" placeholder=\\\"请输入手机号码\\\"> </div> <div class=\\\"mui-input-row\\\"> <label>邮箱</label> <input type=\\\"text\\\" name=\\\"email\\\" value=\\\"{$LoginBusiness.email}\\\" class=\\\"mui-input-clear\\\" placeholder=\\\"请输入邮箱\\\"> </div> <div class=\\\"mui-input-row\\\"> <label>密码</label> <input type=\\\"password\\\" class=\\\"mui-input-password\\\" placeholder=\\\"密码为空不修改密码\\\"> </div> <div class=\\\"mui-input-row\\\"> <label>性别</label> <select name=\\\"gender\\\"> <option value=\\\"0\\\" {$LoginBusiness.gender == 0 ? 'selected' : ''}>保密</option> <option value=\\\"1\\\" {$LoginBusiness.gender == 1 ? 'selected' : ''}>男</option> <option value=\\\"2\\\" {$LoginBusiness.gender == 2 ? 'selected' : ''}>女</option> </select> </div> <div class=\\\"mui-input-row region\\\"> <label>地区</label> <div class=\\\"citypicker\\\"> <input id=\\\"city\\\" type=\\\"text\\\" data-toggle=\\\"city-picker\\\" name=\\\"region\\\" readonly /> <!-- 隐藏域 --> <input type=\\\"hidden\\\" name=\\\"code\\\" id=\\\"code\\\"> </div> </div> <input type=\\\"file\\\" class=\\\"form-control\\\" id=\\\"avatar\\\" name=\\\"avatar\\\" style=\\\"display: none;\\\" /> <div class=\\\"mui-button-row\\\"> <button type=\\\"button\\\" class=\\\"mui-btn mui-btn-primary\\\">确认</button> <button type=\\\"button\\\" class=\\\"mui-btn mui-btn-danger\\\" onclick=\\\"history.go(-1)\\\">返回</button> </div> </form> <script src=\\\"/assets/home/plugin/city/js/city-picker.data.min.js\\\"></script> <script src=\\\"/assets/home/plugin/city/js/city-picker.min.js\\\"></script> <script> // 选择地区 $(\\\"#city\\\").on(\\\"cp:updated\\\", function() { var citypicker = $(this).data(\\\"citypicker\\\"); var code = citypicker.getCode(\\\"district\\\") || citypicker.getCode(\\\"city\\\") || citypicker.getCode(\\\"province\\\"); $(\\\"#code\\\").val(code); }); // 选择头像 $('.avatar').click(function(){ $('#avatar').click(); }); // 更新头像预览 $('#avatar').change(function(){ let file = $(this)[0].files[0]; if(file) { let reader = new FileReader(); reader.readAsDataURL(file); reader.onload = function() { $('.avatar img').attr('src',reader.result); } } }); </script> \"]},\"616\":{\"h\":\"📝执行sql语句添加地区表\",\"t\":[\"从网盘下载数据库文件，运行 fa_region.sql\",\"https://pan.baidu.com/s/1n4rrc7dmZlUwmGgbHkuuyg?pwd=hw66 \"]},\"617\":{\"h\":\"创建地区模型\",\"t\":[\"application\\\\common\\\\model\\\\Region.php\",\"<?php namespace app\\\\common\\\\model; use think\\\\Model; class Region extends Model { // 表名 protected $name = 'region'; } \"]},\"618\":{\"h\":\"✨修改功能流程图\"},\"619\":{\"h\":\"✨修改资料功能\",\"t\":[\"application\\\\home\\\\controller\\\\Business.php\",\"详情\",\" /** * 修改资料 */ public function profile() { if ($this->request->isPost()) { // 接收表单里的所有参数 $params = $this->request->param(); // 封装更新数据 $data = [ // 获取登录用户的id作为更新条件 'id' => $this->LoginBusiness['id'], 'nickname' => $params['nickname'], 'email' => $params['email'], 'gender' => $params['gender'] ]; // 如果邮箱更改的话，那么需要重新认证 if ($params['email'] != $this->LoginBusiness['email']) { $data['auth'] = 0; } // 如果更新密码 if (!empty($params['password'])) { // 验证密码 if (password_verify($params['password'] . $this->LoginBusiness['salt'], $this->LoginBusiness['password'])) { $this->error('新密码不能与原密码一致'); } // 生成密码盐和哈希密码 $salt = build_ranstr(); $hashedPassword = password_hash($params['password'] . $salt, PASSWORD_DEFAULT); // 把处理好的密码追加更新数据 $data['salt'] = $salt; $data['password'] = $hashedPassword; } // 地区 if (!empty($params['code'])) { $path = model('Region')->where(['code' => $params['code']])->value('parentpath'); if (empty($path)) { $this->error('所选地区不存在'); } $pathArr = explode(',', $path); $data['province'] = $pathArr[0] ?? null; $data['city'] = $pathArr[1] ?? null; $data['district'] = $pathArr[2] ?? null; } // 头像上传 if (isset($_FILES['avatar']) && $_FILES['avatar']['size'] > 0) { $res = build_upload('avatar'); if ($res['code'] === 0) { $this->error($res['msg']); } $data['avatar'] = $res['data']; } // 更新数据表 $result = $this->BusinessModel->validate('common/business/Business.profile')->isUpdate(true)->save($data); if ($result === false) { if (isset($data['avatar']) && $_FILES['avatar']['size']) { @is_file(ltrim($data['avatar'], '/')) && @unlink($data['avatar'], '/'); } $this->error($this->BusinessModel->getError()); } else { if (isset($data['avatar']) && $_FILES['avatar']['size']) { @is_file(ltrim($this->LoginBusiness['avatar'], '/')) && @unlink($this->LoginBusiness['avatar'], '/'); } $this->success('更新成功', url('/home/business/index')); } } return $this->fetch(); } \"]},\"620\":{\"h\":\"资料修改页面\",\"t\":[\"图片路径读取变量\",\"<img src=\\\"{$LoginBusiness.avatar_cdn}\\\" /> \",\"给密码添加上name\",\" <input type=\\\"password\\\" class=\\\"mui-input-password\\\" placeholder=\\\"密码为空不修改密码\\\"> \",\"确认按钮type从button改为submit\",\"<button type=\\\"submit\\\" class=\\\"mui-btn mui-btn-primary\\\">确认</button> \",\"从全局中获取省市区的值更新到表单中\",\"// 获取省市区 let province = `{$LoginBusiness.province}`; let city = `{$LoginBusiness.city}`; let district = `{$LoginBusiness.district}`; let code = district || city || province; // 更新地区的值 $('#city').val(code); $('#code').val(code); \"]},\"621\":{\"h\":\"单图上传工具函数\",\"t\":[\"application\\\\common.php\",\" if(!function_exists('build_upload')) { /** * 单图上传 * @param String $name 图片名称 * @return Array 返回结果集 */ function build_upload($name) { $result = [ 'code' => 0, 'msg' => '没有图片上传', 'data' => null ]; $file = request()->file($name); if($file) { // 把图片保存指定的目录 $info = $file->move(ROOT_PATH . 'public' . DS . 'uploads'); if($info) { // 修正路径的斜杠 $fileName = str_replace('\\\\\\\\','/',$info->getSaveName()); $result = [ 'code' => 1, 'msg' => '上传成功', 'data' => '/uploads/' . $fileName ]; }else{ $result['msg'] = $file->getError(); } } return $result; } } \"]},\"622\":{\"h\":\"邮箱验证\"},\"623\":{\"h\":\"邮箱认证是否显示\",\"t\":[\"application\\\\home\\\\view\\\\business\\\\index.html\",\" {if condition=\\\"$LoginBusiness.auth == 0\\\"} <a href=\\\"{:url('/home/business/email')}\\\" class=\\\"item\\\"> <span>邮箱认证</span> <img src='/assets/home/images/row.png' /> </a> {/if} \"]},\"624\":{\"h\":\"在后台邮件配置增加SMTP授权码\"},\"625\":{\"h\":\"controller\",\"t\":[\"application\\\\home\\\\controller\\\\Business.php\",\" /** * 邮箱 */ public function email() { return $this->fetch(); } \"]},\"626\":{\"h\":\"邮箱认证页面\",\"t\":[\"application\\\\home\\\\view\\\\business\\\\email.html\",\"<link rel=\\\"stylesheet\\\" href=\\\"/assets/home/css/profile.css\\\"> <div class=\\\"user\\\"> <div class=\\\"avatar wow fadeInUp\\\"> <img src=\\\"{$LoginBusiness.avatar_cdn}\\\" /> </div> </div> <form class=\\\"mui-input-group\\\" method=\\\"post\\\" enctype=\\\"multipart/form-data\\\"> <div class=\\\"mui-input-row\\\"> <label>邮箱</label> <input type=\\\"text\\\" name=\\\"email\\\" value=\\\"{$LoginBusiness.email}\\\" class=\\\"mui-input-clear\\\" placeholder=\\\"请输入邮箱\\\" required disabled /> </div> <div class=\\\"mui-input-row\\\"> <label>验证码</label> <input type=\\\"text\\\" name=\\\"code\\\" class=\\\"mui-input-clear\\\" placeholder=\\\"请输入验证码\\\" required /> </div> <div class=\\\"mui-button-row\\\"> <button type=\\\"submit\\\" class=\\\"mui-btn mui-btn-primary\\\">确认</button> <button type=\\\"button\\\" class=\\\"mui-btn mui-btn-danger\\\" onclick=\\\"history.go(-1)\\\">返回</button> <button type=\\\"button\\\" id=\\\"send\\\" class=\\\"mui-btn mui-btn-success\\\">发送邮件</button> </div> </form> \",\"application\\\\home\\\\view\\\\business\\\\email.html\",\"<script> const SECONDS = 60; let timer; $(\\\"#send\\\").click(function () { $.ajax({ type: 'post', url: `{:url('/home/business/send')}`, dataType: 'json', success: function (res) { if (res.code === 0) { mui.toast(res.msg, { duration: 2000 }); return; } //轻提醒 mui.toast(res.msg, { duration: 2000 }); //倒计时 $(this).html(SECONDS); $(this).attr('disabled', true); timer = setInterval(countdown, 1000); } }); }) function countdown() { const $sendBtn = $(\\\"#send\\\") let num = parseInt($sendBtn.html()) if (num <= 0) { clearInterval(timer) $(\\\"#send\\\").removeAttr('disabled') $(\\\"#send\\\").html('重新发送验证码') } else { $sendBtn.html(num - 1) } } </script> \"]},\"627\":{\"h\":\"邮件验证码的验证器\",\"t\":[\"application\\\\common\\\\validate\\\\Ems.php\",\"<?php namespace app\\\\common\\\\validate; use think\\\\Validate; /** * 邮件验证码的验证器 */ class Ems extends Validate { protected $rule = [ 'event' => 'require', 'email' => 'require', 'code' => 'require', 'ip' => 'require', ]; protected $message = [ 'event.require' => '事件必填', 'email.require' => '邮箱必须填写', 'code.require' => '验证码未知', 'ip.require' => 'ip地址未知', ]; } \"]},\"628\":{\"h\":\"发送邮箱\",\"t\":[\"application\\\\home\\\\controller\\\\Business.php\",\"详情\",\" /** * 发送邮件 */ public function send() { if ($this->request->isAjax()) { $email = $this->LoginBusiness['email'] ?? ''; if (empty($email)) { $this->error('邮箱为空'); } // 实例化邮箱验证码模型 $Ems = model('Ems'); // 组装条件 $where = [ 'event' => 'email', 'email' => $email, 'times' => 0 ]; // 查询数据表是否有这条记录 $ems = $Ems->where($where)->find(); if ($ems) { $this->error('已发送验证码,请检查您的邮箱'); } // 实例化邮件类 $mail = new Email(); // 开启事务 $Ems->startTrans(); // 生成验证码 $code = build_ranstr(4); $data = [ 'event' => 'email', 'email' => $email, 'times' => 0, 'ip' => $this->request->ip(), 'code' => $code ]; $EmsStatus = $Ems->validate('common/Ems')->save($data); if ($EmsStatus === false) { $this->error($Ems->getError()); } // 正文内容 $html = \\\"<div>您的邮箱认证码为：<b>$code</b></div>\\\"; // 获取发送人的邮箱 $FromEmail = config('site.mail_from'); $res = $mail->from($FromEmail, '云平台')->subject('邮箱认证')->message($html)->to($email)->send(); if ($res === false) { // 回滚事务 $Ems->rollback(); $this->error($mail->getError()); } else { // 提交事务 $Ems->commit(); $this->success('发送成功'); } } } \"]},\"629\":{\"h\":\"邮箱认证功能\",\"t\":[\"application\\\\home\\\\controller\\\\Business.php\",\"详情\",\" /** * 邮箱 */ public function email() { if ($this->request->isPost()) { $code = $this->request->param('code', '', 'trim'); $email = $this->LoginBusiness['email'] ?? ''; if (empty($code)) { $this->error('验证码不能为空'); } if (empty($email)) { $this->error('邮箱地址为空'); } $where = [ 'event' => 'email', 'code' => $code, 'email' => $email, 'times' => 0 ]; $Ems = model('Ems'); $ems = $Ems->where($where)->find(); if (!$ems) { $this->error('验证码错误'); } $this->BusinessModel->startTrans(); // 开启事务 $Ems->startTrans(); // 更新用户的数据 $BusinessData = [ 'id' => $this->LoginBusiness['id'], 'auth' => 1 ]; // 更新结果 $BusinessStatus = $this->BusinessModel->isUpdate(true)->save($BusinessData); if ($BusinessStatus === false) { $this->error($this->BusinessModel->getError()); } // 删除验证码 $EmsStatus = $Ems->destroy($ems['id']); if ($EmsStatus === false) { $this->BusinessModel->rollback(); $this->error($Ems->getError()); } if ($BusinessStatus === false || $EmsStatus === false) { $this->BusinessModel->rollback(); $Ems->rollback(); $this->error('认证失败'); } else { $this->BusinessModel->commit(); $Ems->commit(); $this->success('认证成功', url('/home/business/index')); } } return $this->fetch(); } \"]},\"630\":{\"h\":\"代码重构优化\",\"t\":[\"1.减少不必要的条件判断： 可以通过合并验证条件，减少判断的复杂性。例如，将判断验证码和邮箱是否为空的条件合并为一个条件。 2.减少数据库查询次数： 在代码中有两次查询数据库的操作，可以考虑将它们合并为一次查询，以减少数据库访问的次数。 3.简化事务处理： 使用数据库事务时，可以使用 try-catch 语句来简化事务处理逻辑。\",\"详情\",\" /** * 邮箱 */ public function email() { if ($this->request->isPost()) { $code = $this->request->param('code', '', 'trim'); $email = $this->LoginBusiness['email'] ?? ''; if (empty($code) || empty($email)) { $this->error('验证码或邮箱地址不能为空'); } $where = [ 'event' => 'email', 'code' => $code, 'email' => $email, 'times' => 0 ]; $Ems = model('Ems'); // 开启事务 $this->BusinessModel->startTrans(); $Ems->startTrans(); try { $ems = $Ems->where($where)->find(); if (!$ems) { throw new \\\\Exception('验证码错误'); } $BusinessData = [ 'id' => $this->LoginBusiness['id'], 'auth' => 1 ]; $BusinessStatus = $this->BusinessModel->isUpdate(true)->save($BusinessData); if ($BusinessStatus === false) { throw new \\\\Exception($this->BusinessModel->getError()); } $EmsStatus = $Ems->destroy($ems['id']); if ($EmsStatus === false) { throw new \\\\Exception($Ems->getError()); } $this->BusinessModel->commit(); $Ems->commit(); } catch (\\\\Exception $e) { $this->BusinessModel->rollback(); $Ems->rollback(); $this->error($e->getMessage()); } $this->success('认证成功', url('/home/business/index')); } return $this->fetch(); } \"]},\"631\":{\"h\":\"搜索页_详情页\"},\"632\":{\"h\":\"创建controller\",\"t\":[\"php think make:controller home/subject/Subject \",\"<?php namespace app\\\\home\\\\controller\\\\subject; use app\\\\common\\\\controller\\\\Home; class Subject extends Home { public function search() { return $this->fetch(); } } \"]},\"633\":{\"h\":\"导入数据库\",\"t\":[\"https://pan.baidu.com/s/1QcFT3_MijpKUChoyOYBitg?pwd=hw66 \"]},\"634\":{\"h\":\"页面修改\",\"t\":[\"application\\\\home\\\\view\\\\common\\\\footer.html 搜索按钮地址修改\",\"<a href=\\\"{:url('/home/subject/subject/search')}\\\"> <span class=\\\"mui-icon mui-icon-search\\\"></span> <span class=\\\"mui-tab-label\\\">搜索</span> </a> \",\"将其原先用if判断高亮tabber的js代码换成switch语句\",\" const tabItem = $('.footer-bar .tab-item'); switch (path) { case '': case '/': tabItem.eq(0).addClass('mui-active') break; case 'home/subject': tabItem.eq(1).addClass('mui-active') break; case 'home/business': tabItem.eq(2).addClass('mui-active') break; } \"]},\"635\":{\"h\":\"Model层\"},\"636\":{\"h\":\"subject\",\"t\":[\"application/common/model/subject/Subject.php\",\"<?php namespace app\\\\common\\\\model\\\\subject; use think\\\\Model; class Subject extends Model { //模型对应的是哪张表 protected $name = \\\"subject\\\"; //指定一个自动设置的时间字段 //开启自动写入 protected $autoWriteTimestamp = true; //设置字段的名字 protected $createTime = \\\"createtime\\\"; //插入的时候设置的字段名 //禁止 写入的时间字段 protected $updateTime = false; // 追加属性 protected $append = [ 'likes_count', 'thumbs_cdn', // 课程封面 'createtime_text' ]; public function getThumbsCdnAttr($value, $data) { // 获取网站域名 $cdn = config('site.url'); $thumbs = empty($data['thumbs']) ? '/assets/img/coures.jpg' : $data['thumbs']; return $cdn . $thumbs; } //获取器方法 public function getLikesCountAttr($value, $data) { $likes = trim($data['likes']); //字符串转化为数组 $likes = explode(',', $data['likes']); $likes = array_filter($likes); return count($likes); } public function getCreatetimeTextAttr($value,$data) { $createtime = $data['createtime'] ?? ''; return date(\\\"Y-m-d\\\",$createtime); } // 关联查询 public function category() { return $this->belongsTo('app\\\\common\\\\model\\\\subject\\\\Category','cateid','id',[],'LEFT')->setEagerlyType(0); } } \"]},\"637\":{\"h\":\"Comment\",\"t\":[\"application\\\\common\\\\model\\\\subject\\\\Comment.php\",\"<?php namespace app\\\\common\\\\model\\\\subject; use think\\\\Model; class Comment extends Model { protected $name = 'subject_comment'; // 关联查询方法 public function business() { return $this->belongsTo('app\\\\common\\\\model\\\\business\\\\Business','busid','id',[],'LEFT')->setEagerlyType(0); } } \"]},\"638\":{\"h\":\"Chapter\",\"t\":[\"application\\\\common\\\\model\\\\subject\\\\Chapter.php\",\"<?php namespace app\\\\common\\\\model\\\\subject; use think\\\\Model; class Chapter extends Model { protected $name = 'subject_chapter'; } \"]},\"639\":{\"h\":\"Category\",\"t\":[\"application\\\\common\\\\model\\\\subject\\\\Category.php\",\"<?php namespace app\\\\common\\\\model\\\\subject; use think\\\\Model; class Category extends Model { protected $name = 'subject_category'; } \"]},\"640\":{\"h\":\"View\"},\"641\":{\"h\":\"创建search.html\",\"t\":[\"application\\\\home\\\\view\\\\subject\\\\subject\\\\search.html\",\"<link rel=\\\"stylesheet\\\" href=\\\"/assets/home/plugin/mescroll/mescroll.min.css\\\"> <link rel=\\\"stylesheet\\\" href=\\\"/assets/home/css/search.css\\\"> <!-- 搜索框 --> <div class=\\\"mui-input-row mui-search\\\"> <input type=\\\"search\\\" class=\\\"mui-input-clear\\\" placeholder=\\\"请输入课程名称\\\" name=\\\"search\\\" /> </div> <!-- 课程列表 --> <div id=\\\"subject-list\\\" class=\\\"mescroll mui-content\\\"> <ul class=\\\"mui-table-view\\\"> </ul> </div> {include file='common/footer'/} <script src=\\\"/assets/home/plugin/mescroll/mescroll.min.js\\\"></script> <script> // 当前页 var page = 1; // 每页查询多少条 var limit = 10; // 关键词 var search = ''; //初始化下拉刷新插件 var scroll = new MeScroll('subject-list', { down: { //下拉 auto: false, //是否在初始化完毕之后自动执行下拉回调callback; 默认true callback: DownCallback //下拉刷新的回调 }, up: { auto: true, //是否在初始化时以上拉加载的方式自动加载第一页数据; 默认false callback: UpCallback, //上拉回调,此处可简写 page: { size: limit }, toTop: { //配置回到顶部按钮 duration: 300, src: \\\"/assets/home/plugin/mescroll/mescroll-totop.png\\\", //默认滚动到1000px显示,可配置offset修改 offset: 100 }, htmlNodata: '<div class=\\\"more\\\">没有更多了..</div>', } }); // 下拉刷新 function DownCallback() { // 重置加载状态 清空page 直接从第一页开始 scroll.resetUpScroll(); $('#subject-list ul').empty(); } // 上拉加载 function UpCallback(current) { // 当前页 page = current.num; // 每页显示多少条数据 limit = current.size; // 封装数据 let data = { page: page, limit: limit, search: search } $.ajax({ type: 'post', url: `{:url('/home/subject/subject/search')}`, data: data, dataType: 'json', success: function (res) { if (res.code === 0) { // 隐藏下拉刷新和上拉加载的状态 scroll.endBySize(0, 0); return; } let count = res.data.count; let list = res.data.list; // 隐藏下拉刷新和上拉加载的状态 scroll.endBySize(list.length, count); // 输出数据 fetchList(list); } }); } function fetchList(list) { let temp = ''; for (let item of list) { temp += ` <li class=\\\"mui-table-view-cell mui-media\\\"> <a class=\\\"item\\\" href=\\\"{:url('/home/subject/subject/info')}?id=${item.id}\\\"> <div class=\\\"thumb\\\"> <img class=\\\"img\\\" src=\\\"${item.thumbs_cdn}\\\"> </div> <div class=\\\"info\\\"> <h5 class=\\\"title\\\">${item.title}</h5> <p class=\\\"content mui-ellipsis\\\">课程分类：${item.category.name}</p> <p class=\\\"content mui-ellipsis\\\">创建时间：${item.createtime_text}</p> <p class=\\\"bottom\\\"> <span class=\\\"like mui-icon mui-icon-starhalf\\\"> <span>${item.likes_count}</span> </span> <span class=\\\"price\\\">￥${item.price}</span> </p> </div> </a> </li> ` } $('#subject-list ul').append(temp); } </script> \"]},\"642\":{\"h\":\"创建info.html\",\"t\":[\"application\\\\home\\\\view\\\\subject\\\\subject\\\\info.html\",\"<link rel=\\\"stylesheet\\\" href=\\\"/assets/home/css/details.css\\\"> <div id=\\\"cover\\\"> <!-- 课程封面 --> <img class=\\\"cover_bg\\\" src=\\\"{$subject.thumbs_cdn}\\\"> <!-- 中间播放键 --> <div id=\\\"play\\\"> <img src=\\\"/assets/home/images/play.png\\\" /> </div> </div> <!-- ckplayer视频容器元素 --> <div id=\\\"video\\\"></div> <!-- 课程信息 --> <div class=\\\"head\\\"> <div class=\\\"info\\\"> <h4 class=\\\"name\\\">{$subject.title}</h4> <div class=\\\"action\\\"> <!-- 点赞 --> <!-- <img class=\\\"thumbs\\\" src=\\\"./assets/images/thumbs-up.png\\\" alt=\\\"\\\"> --> <!-- 未点赞 --> <img class=\\\"thumbs\\\" src=\\\"{$subject.like_status ? '/assets/home/images/thumbs-up.png' : '/assets/home/images/thumbs.png'}\\\" alt=\\\"\\\"> <span class=\\\"num\\\">{$subject.likes_count}</span> </div> </div> </div> <div class=\\\"title\\\">课程简介</div> <table class=\\\"list\\\"> <tr> <td>课程名称</td> <td>{$subject.title}</td> </tr> <tr class=\\\"price\\\"> <td>课程价格</td> <td>￥{$subject.price}元</td> </tr> <tr> <td>发布时间</td> <td>{$subject.createtime_text}</td> </tr> <tr> <td>章节总数</td> <td>{$count}小节</td> </tr> </table> <div class=\\\"title\\\">课程介绍</div> <div class=\\\"mui-table-view content\\\">{$subject.content}</div> <div class=\\\"title\\\">课程章节</div> <ul class=\\\"mui-table-view\\\"> {if condition=\\\"$count > 0\\\"} {foreach($ChapterList as $item)} <li class=\\\"mui-table-view-cell\\\">{$item.title}</li> {/foreach} {else /} <li class=\\\"mui-table-view-cell\\\">暂无课程章节</li> {/if} </ul> <div class=\\\"title\\\">用户评论</div> <!-- 评论内容 --> <ul class=\\\"mui-table-view\\\"> {if condition=\\\"count($CommentList) > 0\\\"} {foreach($CommentList as $item)} <li class=\\\"mui-table-view-cell mui-media\\\"> <a href=\\\"javascript:;\\\"> <img class=\\\"mui-media-object mui-pull-left userimg\\\" src=\\\"{$item.business.avatar_cdn}\\\"> <div class=\\\"mui-media-body\\\"> {$item.business.nickname ?: '未知昵称'} <p class='mui-ellipsis'>{$item.content}</p> </div> </a> </li> {/foreach} {else /} <li class=\\\"mui-table-view-cell mui-media\\\"> 暂无评论 </li> {/if} </ul> <div class=\\\"more\\\"> <a href=\\\"comment_list.html\\\" class=\\\"mui-btn mui-btn-primary mui-btn-outlined\\\">查看更多评论</a> </div> <!-- 弹出菜单 --> <div id=\\\"sheet1\\\" class=\\\"mui-popover mui-popover-bottom mui-popover-action\\\"> <!-- 可选择菜单 --> <ul class=\\\"mui-table-view\\\"> <li class=\\\"mui-table-view-cell\\\"> <a id=\\\"buy\\\" href=\\\"javascript:void(0)\\\"> 立即购买 </a> </li> </ul> <!-- 取消菜单 --> <ul class=\\\"mui-table-view\\\"> <li class=\\\"mui-table-view-cell\\\"> <a href=\\\"#sheet1\\\"><b>取消</b></a> </li> </ul> </div> <!-- 加载ckplayer视频插件 --> <script src=\\\"/assets/home/plugin/ckplayer/ckplayer.min.js\\\"></script> <script> $(\\\"#iosActionsheetCancel\\\").click(function () { mui('#sheet1').popover('toggle'); }) // 点击播放 $(\\\"#play\\\").on('click', function () { Paydata() }) function Paydata() { mui('#sheet1').popover('toggle'); } $(\\\"#buy\\\").on('click', function () { mui('#sheet1').popover('toggle'); var btnArray = ['否', '是']; mui.confirm('请问您确认购买吗？', '尊敬的用户', btnArray, function (e) { if (e.index = 1) { location.href = \\\"./assets/buy.html\\\"; } }) }) </script> \",\"加入mescroll插件到public\\\\assets\\\\home\\\\plugin\"]},\"643\":{\"h\":\"Controller层\",\"t\":[\"application\\\\home\\\\controller\\\\subject\\\\Subject.php\",\"<?php namespace app\\\\home\\\\controller\\\\subject; use app\\\\common\\\\controller\\\\Home; class Subject extends Home { protected $noNeedLogin = ['info']; // 课程模型 protected $SubjectModel = null; // 课程章节模型 protected $ChapterModel = null; // 课程评论模型 protected $CommentModel = null; // 课程订单模型 protected $OrderModel = null; // 用户模型 protected $BusinessModel = null; // 用户消费记录模型 protected $RecordModel = null; public function __construct() { parent::__construct(); $this->SubjectModel = model('subject.Subject'); $this->ChapterModel = model('subject.Chapter'); $this->CommentModel = model('subject.Comment'); $this->OrderModel = model('subject.Order'); $this->BusinessModel = model('business.Business'); $this->RecordModel = model('business.Record'); } public function search() { if($this->request->isAjax()) { $page = $this->request->param('page',1,'trim'); $limit = $this->request->param('limit',10,'trim'); $search = $this->request->param('search','','trim'); // 条件数组 $where = []; if($search) { $where['title'] = ['LIKE',\\\"%$search%\\\"]; } $count = $this->SubjectModel->where($where)->count(); $list = $this->SubjectModel->with(['category'])->where($where)->order('createtime DESC')->page($page,$limit)->select(); if($list) { $this->success('查询成功',null,['count' => $count,'list' => $list]); }else{ $this->error('暂无数据',null,['count' => 0,'list' => []]); } } return $this->fetch(); } public function info() { $id = $this->request->param('id','','trim'); $subject = $this->SubjectModel->find($id); if(!$subject) { $this->error('课程不存在'); } // 获取cookie里面的信息 $business = cookie('LoginBusiness'); $subject['like_status'] = false; if($business) { $likeArr = explode(',',$subject['likes']); // 过滤空元素 $likeArr = array_filter($likeArr); if(in_array($business['id'],$likeArr)) { $subject['like_status'] = true; } } // 获取该课程的章节的总数 $count = $this->ChapterModel->where(['subid' => $id])->count(); // 获取该课程的所有章节的数据 $ChapterList = $this->ChapterModel->where(['subid' => $id])->order('id ASC')->select(); // 获取对该课程的评价 $CommentList = $this->CommentModel->with(['business'])->where(['subid' => $id])->limit(5)->order('createtime DESC')->select(); $this->assign([ 'subject' => $subject, 'count' => $count, 'ChapterList' => $ChapterList, 'CommentList' => $CommentList ]); return $this->fetch(); } public function like() { $subid = $this->request->param('subid',0,'trim'); $subject = $this->SubjectModel->find($subid); if(!$subject) { $this->error('课程不存在'); } $likeArr = explode(',',$subject['likes']); $likeArr = array_filter($likeArr); $msg = ''; if(in_array($this->LoginBusiness['id'],$likeArr)) { $index = array_search($this->LoginBusiness['id'],$likeArr); if(!$index) { $this->error('无法取消点赞'); } unset($likeArr[$index]); $msg = '取消点赞'; }else{ $likeArr[] = $this->LoginBusiness['id']; $msg = '点赞'; } // 封装更新数据 $data = [ 'id' => $subid, 'likes' => implode(',',$likeArr) ]; $result = $this->SubjectModel->isUpdate(true)->save($data); if($result === false) { $this->error($msg . '失败'); }else{ $this->success($msg . '成功'); } } public function play() { $subid = $this->request->param('subid',0,'trim'); $cid = $this->request->param('cid',0,'trim'); $subject = $this->SubjectModel->find($subid); if(!$subject) { $this->error('课程不存在'); } $OrderWhere = [ 'busid' => $this->LoginBusiness['id'], 'subid' => $subid ]; $order = $this->OrderModel->where($OrderWhere)->find(); if(!$order) { $this->error('请先购买课程',null,['buy' => 'buy']); } } public function buy() { $subid = $this->request->param('subid',0,'trim'); // 查询这个课程是否存在 $subject = $this->SubjectModel->find($subid); if(!$subject) { $this->error('课程不存在'); } // 查询这个用户是否购买过了这个课程 $OrderWhere = [ 'busid' => $this->LoginBusiness['id'], 'subid' => $subid ]; $order = $this->OrderModel->where($OrderWhere)->find(); if($order) { $this->error('您已购买过了该课程，无须购买'); } // 余额是否够购买该课程 $UpdateMoney = bcsub($this->LoginBusiness['money'],$subject['price'],2); if($UpdateMoney < 0) { $this->error('余额不足，请及时充值'); } /* 订单表 => 新增一个订单记录 用户表 => 更新余额 用户消费记录表 => 新增一条记录 */ // 开启事务 $this->OrderModel->startTrans(); $this->BusinessModel->startTrans(); $this->RecordModel->startTrans(); // 封装订单数据 $OrderData = [ 'subid' => $subid, 'busid' => $this->LoginBusiness['id'], 'total' => $subject['price'], 'code' => build_code('SU'), ]; // 把数据插入数据表 $OrderStatus = $this->OrderModel->validate('common/subject/Order')->save($OrderData); if($OrderStatus === false) { $this->error($this->OrderModel->getError()); } // 更新用户数据 $BusinessData = [ 'id' => $this->LoginBusiness['id'], 'money' => $UpdateMoney ]; // 自定义一个验证器 $validate = [ // 规则 [ 'money' => ['number', '>=:0'], ], // 错误信息 [ 'money.number' => '余额必须是数字类型', 'money.>=' => '余额必须大于等于0元', ] ]; $BusinessStatus = $this->BusinessModel->validate(...$validate)->isUpdate(true)->save($BusinessData); if($BusinessStatus === false) { $this->OrderModel->rollback(); $this->error($this->BusinessModel->getError()); } // 封装用户消费记录 $RecordData = [ 'total' => $subject['price'], 'busid' => $this->LoginBusiness['id'], 'content' => \\\"购买课程：【{$subject['title']}】花费了 ￥{$subject['price']} 元\\\" ]; $RecordStatus = $this->RecordModel->validate('common/business/Record')->save($RecordData); if($RecordStatus === false) { $this->OrderModel->rollback(); $this->BusinessModel->rollback(); $this->error($this->RecordModel->getError()); } if($OrderStatus === false || $BusinessStatus === false || $RecordStatus === false) { $this->OrderModel->rollback(); $this->BusinessModel->rollback(); $this->RecordModel->rollback(); $this->error('购买课程失败'); }else{ $this->OrderModel->commit(); $this->BusinessModel->commit(); $this->RecordModel->commit(); $this->success('购买课程成功'); } } } \"]},\"644\":{\"h\":\"点赞功能_课程购买\"},\"645\":{\"h\":\"点赞\",\"t\":[\"application\\\\home\\\\view\\\\subject\\\\subject\\\\info.html\",\" // 点赞以及取消点赞 $('.action').click(function () { $.ajax({ type: 'post', url: `{:url('/home/subject/subject/like')}`, data: { subid: `{$subject.id}` }, dataType: 'json', success: function (res) { if (res.code === 1) { let status = res.msg.includes('取消点赞成功') if (status === true) { $('.thumbs').attr('src', '/assets/home/images/thumbs.png'); $('.num').text(parseInt($('.num').text()) - 1); } else { $('.thumbs').attr('src', '/assets/home/images/thumbs-up.png'); $('.num').text(parseInt($('.num').text()) + 1); } return; } else { mui.toast(res.msg) return } } }) }) \",\"application\\\\home\\\\controller\\\\subject\\\\Subject.php\",\" /** * 点赞 */ public function like() { $subid = $this->request->param('subid', 0, 'trim'); // 查询课程是否存在 $subject = $this->SubjectModel->find($subid); if (!$subject) { $this->error('课程不存在'); } // 分割数组 $likeArr = explode(',', $subject['likes']); // 过滤空元素 $likeArr = array_filter($likeArr); $msg = ''; if (in_array($this->LoginBusiness['id'], $likeArr)) { $index = array_search($this->LoginBusiness['id'], $likeArr); if ($index !== false) { unset($likeArr[$index]); $msg = '取消点赞'; } else { $this->error('无法取消点赞'); } } else { $likeArr[] = $this->LoginBusiness['id']; $msg = '点赞'; } // 封装更新数据 $data = [ 'id' => $subid, 'likes' => implode(',', $likeArr) ]; $result = $this->SubjectModel->isUpdate(true)->save($data); if ($result === false) { $this->error($msg . '失败'); } else { $this->success($msg . '成功'); } } \"]},\"646\":{\"h\":\"章节序号显示\",\"t\":[\"application\\\\home\\\\view\\\\subject\\\\subject\\\\info.html\",\"第58行，给foreach添加上$key，给章节标题前添加上序号，给li表填绑定toggle方法传入id\",\"{foreach($ChapterList as $key=> $item)} <li onclick=\\\"toggle('{$item.id}')\\\" class=\\\"mui-table-view-cell\\\">{$key+1}.{$item.title}</li> {/foreach} \"]},\"647\":{\"h\":\"点击视频判断是否购买课程\"},\"648\":{\"h\":\"Model层\"},\"649\":{\"h\":\"order\",\"t\":[\"application\\\\common\\\\model\\\\subject\\\\Order.php\",\"<?php namespace app\\\\common\\\\model\\\\subject; use think\\\\Model; class Order extends Model { //模型对应的是哪张表 protected $name = \\\"subject_order\\\"; //指定一个自动设置的时间字段 //开启自动写入 protected $autoWriteTimestamp = true; //设置字段的名字 protected $createTime = \\\"createtime\\\"; //插入的时候设置的字段名 //禁止 写入的时间字段 protected $updateTime = false; } \"]},\"650\":{\"h\":\"View层\",\"t\":[\"application\\\\home\\\\view\\\\subject\\\\subject\\\\info.html\",\" // 课程id let subid = \\\"{$subject.id}\\\"; // 定义一个章节的id let cid = 0; // 播放器对象 let player; // 点击播放 $(\\\"#play\\\").on('click', function () { $.ajax({ type: 'post', url: `{:url('/home/subject/subject/play')}`, data: { subid, cid }, dataType: 'json', success: function (res) { if (res.code === 0) { mui.toast(res.msg); if (res.data.buy === true) { mui('#sheet1').popover('toggle'); } return; } else { mui.toast(res.msg, { duration: 1500 }) } } }) }); \"]},\"651\":{\"h\":\"Controller层\",\"t\":[\"添加model\",\"application\\\\home\\\\controller\\\\subject\\\\Subject.php\",\" // 课程订单模型 protected $OrderModel = null; public function __construct() { // .... $this->OrderModel = model('subject.Order'); } \",\" /** * 播放 */ public function play() { $subid = $this->request->param('subid', 0, 'trim'); $cid = $this->request->param('cid', 0, 'trim'); $subject = $this->SubjectModel->find($subid); if (!$subject) { $this->error('课程不存在'); } $OrderWhere = [ 'busid' => $this->LoginBusiness['id'], 'subid' => $subid ]; $order = $this->OrderModel->where($OrderWhere)->find(); if (!$order) { $this->error('请先购买课程', null, ['buy' => 'buy']); } } \"]},\"652\":{\"h\":\"课程购买\",\"t\":[\"导入数据表fa_business_record.sql\"]},\"653\":{\"h\":\"Model层\"},\"654\":{\"h\":\"Record\",\"t\":[\"application\\\\common\\\\model\\\\business\\\\Record.php\",\"<?php namespace app\\\\common\\\\model\\\\business; use think\\\\Model; class Record extends Model { //模型对应的是哪张表 protected $name = \\\"business_record\\\"; //指定一个自动设置的时间字段 //开启自动写入 protected $autoWriteTimestamp = true; //设置字段的名字 protected $createTime = \\\"createtime\\\"; //插入的时候设置的字段名 //禁止 写入的时间字段 protected $updateTime = false; } \"]},\"655\":{\"h\":\"字段验证\",\"t\":[\"application\\\\common\\\\validate\\\\business\\\\Record.php\",\"<?php namespace app\\\\common\\\\validate\\\\business; use think\\\\Validate; // 用户消费记录的验证器 class Record extends Validate { protected $rule = [ 'total' => 'require', 'busid' => 'require', 'content' => 'require', ]; protected $message = [ 'total.require' => '消费金额必填', 'busid.require' => '用户必须填写', 'content.require' => '消费描述必须填写', ]; } \",\"application\\\\common\\\\validate\\\\subject\\\\Order.php\",\"<?php namespace app\\\\common\\\\validate\\\\subject; use think\\\\Validate; // 课程订单的验证器 class Order extends Validate { protected $rule = [ 'subid' => 'require', 'busid' => 'require', 'total' => 'require', 'code' => ['require', 'unique:subject_order'], ]; protected $message = [ 'subid.require' => '课程必须填写', 'busid.require' => '用户必须填写', 'total.require' => '消费金额必须填写', 'code.require' => '订单号必须填写', 'code.unique' => '订单号已重复', ]; } \"]},\"656\":{\"h\":\"Videw层\",\"t\":[\"application\\\\home\\\\view\\\\subject\\\\subject\\\\info.html\",\" $(\\\"#buy\\\").on('click', function () { mui('#sheet1').popover('toggle'); var btnArray = ['否', '是']; mui.confirm('请问您确认购买吗？', '尊敬的用户', btnArray, function (e) { if (e.index === 1) { $.ajax({ type: 'post', url: `{:url('/home/subject/subject/buy')}`, data: { subid }, dataType: 'json', success: function (res) { if (res.code === 1) { mui.toast(res.msg); return; } else { mui.toast(res.msg); return; } } }) } }) }) \"]},\"657\":{\"h\":\"Controller层\"},\"658\":{\"h\":\"新增用户模型与消费记录模型\",\"t\":[\"application\\\\home\\\\controller\\\\subject\\\\Subject.php\",\" // 用户模型 protected $BusinessModel = null; // 消费记录模型 protected $RecordModel = null; public function __construct() { // ..... $this->BusinessModel = model('Business.Business'); $this->RecordModel = model('Business.Record'); } \"]},\"659\":{\"h\":\"添加公共方法-唯一订单号\",\"t\":[\"application\\\\common.php\",\"if(!function_exists('build_code')) { /** * 生成唯一订单号 * @param String $prefix 指定的订单前缀 * @return String 返回字符串 */ function build_code($prefix = \\\"\\\") { @date_default_timezone_set(\\\"PRC\\\"); $order_id_main = date('YmdHis') . rand(10000, 99999); //订单号码主体长度 $order_id_len = strlen($order_id_main); $order_id_sum = 0; for ($i = 0; $i < $order_id_len; $i++) { // 将订单号的主体部分的每个字符提取出来，并将其转换为整数。然后，将这个整数值加到 `$order_id_sum` 上，用于计算订单号的校验位。 $order_id_sum += (int)(substr($order_id_main, $i, 1)); } //唯一订单号码（YYYYMMDDHHIISSNNNNNNNNCC） $osn = $prefix.$order_id_main . str_pad((100 - $order_id_sum % 100) % 100, 2, '0', STR_PAD_LEFT); //生成唯一订单号 return $osn; } } \"]},\"660\":{\"h\":\"雪花算法生成订单\",\"t\":[\" 0 41 51 63 +-------------------+-------------------+-----------+ | Timestamp (ms) | Machine ID (10) | Sequence | +-------------------+-------------------+-----------+ \",\"Timestamp (ms)：时间戳部分占用 41 位，用于表示生成标识符的时间戳，以毫秒为单位。这部分占据标识符的高位部分。\",\"Machine ID (10)：机器 ID 部分占用 10 位，用于表示机器的唯一标识符，以确保在分布式环境中生成的标识符是唯一的。\",\"Sequence：序列号部分占用 12 位，用于表示同一毫秒内生成的多个标识符的序列号，以确保在同一毫秒内生成的标识符是唯一的。这部分占据标识符的低位部分。\",\"在左移位操作中，Timestamp (ms) 部分会被左移 22 位，Machine ID (10) 部分会被左移 12 位，这样它们的位数就会对齐到标识符的相应位置。\",\"示例演示：\",\"假设当前时间戳为\",\"16777215（二进制为 00000000001111111111111111111111，占用 24 位）\",\"Machine ID 为 31（二进制为 0000000000111111，占用 6 位）\",\"Sequence 为 4095（二进制为 0000000000000000111111111111，占用 12 位） 在生成标识符时，左移位操作会将 Timestamp (ms) 左移 22 位，Machine ID 左移 12 位，然后再将它们合并到一起，最终生成一个 64 位的唯一标识符。\",\"/** * 雪花算法 * @param Int $machineId 指定机器码 */ function generateSnowflakeId($machineId) { $epoch = 1630454400000; // 2021-09-01 00:00:00 UTC $sequence = 0; $lastTimestamp = -1; // 返回当前时间的时间戳，以毫秒为单位。 $currentTimestamp = function () { return floor(microtime(true) * 1000); }; // 用于计算下一个毫秒的时间戳，以确保生成的标识符在不同毫秒内是唯一的 $tilNextMillis = function ($lastTimestamp) use ($currentTimestamp) { $timestamp = $currentTimestamp(); while ($timestamp <= $lastTimestamp) { $timestamp = $currentTimestamp(); } return $timestamp; }; // 获取当前时间戳 $timestamp = $currentTimestamp(); // 如果当前时间戳与上一次时间戳相等,表示同一毫秒内 // 那么会根据序列号规则增加序列号，并检查序列号是否达到上限，如果达到上限会等待下一毫秒。 if ($timestamp == $lastTimestamp) { $sequence = ($sequence + 1) & 4095; // 12 bits if ($sequence == 0) { // 等待下一个毫秒 $timestamp = $tilNextMillis($lastTimestamp); } } else { $sequence = 0; } // 更新上一次时间戳为当前时间戳，以便下一次生成时使用。 $lastTimestamp = $timestamp; /** * 这一行计算出标识符。它将时间戳、机器ID和序列号组合成一个整数，以生成最终的唯一标识符。 * 时间戳 41 位 + 机器 ID 10 位 + 序列号 12 位 = 63 位，再加上一个符号位（正数），总共为 64 位。 * 时间戳部分左移 22 位，即占用了标识符的前 41 位。这是因为时间戳部分通常占用了整个 64 位标识符的最高位， * 从而保证生成的标识符在一段时间内是唯一的。左移 22 位是根据上述三个部分的位数来计算的 */ $id = (($timestamp - $epoch) << 22) | ($machineId << 10) | $sequence; return $id; } \"]},\"661\":{\"h\":\"购买课程\",\"t\":[\" /** * 购买课程 */ public function buy() { // 获取课程id $subId = $this->request->param('subid', 0, 'trim'); // 判断课程是否存在 $subject = $this->SubjectModel->find($subId); // 获取用户id $busId = $this->LoginBusiness['id']; if (!$subject) { $this->error('课程不存在'); } // 判断当前登录用户是否购买过 $where = [ 'subid' => $subId, 'busid' => $busId ]; $order = $this->OrderModel->where($where)->find(); if ($order) { $this->error('您已经购买过了该课程，无须重复购买'); } // 课程价格 $price = $subject['price']; // 个人余额 $money = $this->LoginBusiness['money']; // 判断余额是否能够购买 余额-价格 $updateMoney = bcsub($money, $price, 2); if ($updateMoney < 0) { $this->error('余额不足，请先充值'); } // 开启事务操作 $this->BusinessModel->startTrans(); $this->OrderModel->startTrans(); $this->RecordModel->startTrans(); // 插入订单表 // 封装订单数据 $OrderData = [ 'subid' => $subId, 'busid' => $busId, 'total' => $price, 'code' => generateSnowflakeId(1), ]; // 把数据插入数据表 $OrderStatus = $this->OrderModel->validate('common/subject/Order')->save($OrderData); if ($OrderStatus === false) { $this->error($this->OrderModel->getError()); } // 更新用户数据 $BusinessData = [ 'id' => $this->LoginBusiness['id'], 'money' => $updateMoney ]; // 自定义一个验证器 $validate = [ // 规则 [ 'money' => ['number', '>=:0'], ], // 错误信息 [ 'money.number' => '余额必须是数字类型', 'money.>=' => '余额必须大于等于0元', ] ]; $BusinessStatus = $this->BusinessModel->validate(...$validate)->isUpdate(true)->save($BusinessData); if ($BusinessStatus === false) { $this->OrderModel->rollback(); $this->error($this->BusinessModel->getError()); } // 封装用户消费记录 $RecordData = [ 'total' => $subject['price'], 'busid' => $this->LoginBusiness['id'], 'content' => \\\"购买课程：【{$subject['title']}】花费了 ￥{$subject['price']} 元\\\" ]; $RecordStatus = $this->RecordModel->validate('common/business/Record')->save($RecordData); try { if ($RecordStatus === false) { throw new \\\\Exception($this->RecordModel->getError()); } if ($OrderStatus === false || $BusinessStatus === false || $RecordStatus === false) { throw new \\\\Exception('购买课程失败'); } $this->OrderModel->commit(); $this->BusinessModel->commit(); $this->RecordModel->commit(); } catch (\\\\Exception $e) { $this->OrderModel->rollback(); $this->BusinessModel->rollback(); $this->RecordModel->rollback(); $this->error($e->getMessage()); } $this->success('购买课程成功'); } \"]},\"662\":{\"h\":\"播放功能\"},\"663\":{\"h\":\"播放\",\"t\":[\"播放功能使用了ckplayer插件，这是一个无需第三方插件用JS和CSS制作的html5的video播放器插件\",\"注意：本页更新了ckplayer版本为X3，引用路径需调整\",\"<link rel=\\\"stylesheet\\\" href=\\\"/assets/home/plugin/ckplayer/css/ckplayer.css\\\"> <script src=\\\"/assets/home/plugin/ckplayer/js/ckplayer.js\\\"></script> \"]},\"664\":{\"h\":\"Controller\",\"t\":[\"application\\\\home\\\\controller\\\\subject\\\\Subject.php\",\"在play方法中追加\",\" // 查询用户要播放的课程章节 $where = [ 'subid' => $subid ]; if ($cid) { $where['id'] = $cid; } $chapter = $this->ChapterModel->where($where)->order(\\\"id ASC\\\")->find(); if ($chapter) { $this->success('查询成功', null, $chapter); } else { $this->error('暂无章节播放地址'); } \"]},\"665\":{\"h\":\"View\",\"t\":[\"application\\\\home\\\\view\\\\subject\\\\subject\\\\info.html\",\" // 播放器对象 let player = null; // 切换章节 function toggle(id) { cid = id; if (player) { player.pause() player.remove() } $('html, body').animate({ scrollTop: 0 }, 800); $('#cover').css('display', 'block') $('#video').css('display', 'none') } // 点击播放 $(\\\"#play\\\").on('click', function () { $.ajax({ type: 'post', url: `{:url('/home/subject/subject/play')}`, data: { subid, cid }, dataType: 'json', success: function (res) { if (res.code === 0) { mui.toast(res.msg) if (res.data.buy === true) { mui('#sheet1').popover('toggle') } return } let url = res.data?.url; if (!url) { mui.toast(res.msg) return } $('#cover').css('display', 'none') $('#video').css('display', 'block') var videoObject = { container: '#video', //视频容器 video: url, //视频地址 volume: 1, //默认音量 autoplay: true, }; player = new ckplayer(videoObject) } }) }); \"]},\"666\":{\"h\":\"回到顶部功能\",\"t\":[\" $(document).ready(function () { $(window).scroll(function () { if ($(this).scrollTop() > 100) { // 当滚动超过100像素时显示回到顶部按钮 $('#back-to-top').fadeIn(); } else { $('#back-to-top').fadeOut(); } }); $('#back-to-top').click(function () { $('html, body').animate({ scrollTop: 0 }, 800); // 动画返回顶部，速度为800毫秒 }); }); \"]},\"667\":{\"h\":\"day01\",\"t\":[\"问题\",\"1、截取字符串abcdace的ace 2、数组的排序方法\",\"答案\",\"// ?t1:截取字符串abcdace的ace // 1.String.slice let str = \\\"abcdace\\\" let subString = 'ace' let result = '' let index = 0 index = str.indexOf(subString) result = str.slice(index) console.log(\\\"🚀 ~ file: day01.js:9 ~ result:\\\", result) // 2.正则 result = str.match(/ace/g).toString() console.log(\\\"🚀 ~ file: day01.js:12 ~ result:\\\", result) // 3.String.subString result = str.substring(index) console.log(\\\"🚀 ~ file: day01.js:15 ~ result:\\\", result) // ?t2:数组的排序方法 // 数组的排序方法有很多种，最简单的是使用Array的sort方法， 其次是一些常见的排序方法 // 其中冒泡、选择、插入适用于小规模数据，快排、归并适用于大规模数据 // 1.Array.sort let arr = [5, 1, 7, 8, 4, 2] result = arr.sort() console.log(\\\"🚀 ~ file: day01.js:23 ~ result:\\\", result) // 2.冒泡排序 O(n^2) function bubbleSort(arr) { if (arr.length <= 1) { return arr; } let len = arr.length; for (let i = 0; i < len; i++) { for (let j = 0; j < len - i - 1; j++) { if (arr[j] > arr[j + 1]) { let temp = arr[j]; arr[j] = arr[j + 1]; arr[j + 1] = temp; } } } return arr; } result = bubbleSort(arr) console.log(\\\"🚀 ~ file: day01.js:44 ~ result:\\\", result) // 3.选择排序 O(n^2) function selectionSort(arr) { if (arr.length <= 1) { return arr; } let len = arr.length for (let i = 0; i < len - 1; i++) { let minIndex = i for (let j = i + 1; j < len; j++) { if (arr[i] < arr[minIndex]) { minIndex = j } } if (minIndex !== i) { let temp = arr[i] arr[i] = arr[minIndex] arr[minIndex] = temp } } return arr } result = selectionSort(arr) console.log(\\\"🚀 ~ file: day01.js:69 ~ result:\\\", result) // 4.快速排序 O(n log n) function quickSort(arr) { if (arr.length <= 1) { return arr; } let pivot = arr[Math.floor(arr.length / 2)] let less = [] let greater = [] let equal = [] let len = arr.length for (let i = 0; i < len; i++) { if (arr[i] < pivot) { less.push(arr[i]) } else if (arr[i] > pivot) { greater.push(arr[i]) } else { equal.push(arr[i]) } } return quickSort(less).concat(equal, quickSort(greater)) } result = quickSort(arr) console.log(\\\"🚀 ~ file: day01.js:92 ~ result:\\\", result) // 4.插入排序 O(n^2) function insertionSort(arr) { if (arr.length <= 1) { return arr; } let len = arr.length for (let i = 1; i < len; i++) { let key = arr[i] let j = i - 1 while (j >= 0 && arr[j] > key) { arr[j + 1] = arr[j] j-- } arr[j + 1] = key } return arr } result = insertionSort(arr) console.log(\\\"🚀 ~ file: day01.js:113 ~ result:\\\", result) \",\"🚀 ~ file: day01.js:9 ~ result: ace 🚀 ~ file: day01.js:12 ~ result: ace 🚀 ~ file: day01.js:15 ~ result: ace 🚀 ~ file: day01.js:23 ~ result: [ 1, 2, 4, 5, 7, 8 ] 🚀 ~ file: day01.js:44 ~ result: [ 1, 2, 4, 5, 7, 8 ] 🚀 ~ file: day01.js:69 ~ result: [ 1, 2, 4, 5, 7, 8 ] 🚀 ~ file: day01.js:92 ~ result: [ 1, 2, 4, 5, 7, 8 ] 🚀 ~ file: day01.js:113 ~ result: [ 1, 2, 4, 5, 7, 8 ] \"]},\"668\":{\"h\":\"day02\",\"t\":[\"问题\",\"有几种基本数据类型?，什么是复杂数据类型?\",\"事件的代理/委托原理、优缺点、以及如何实现？\",\"答案\",\"7种基本数据类型 Number、String、Boolean、Null、Undefined、Symbol和BigInt\",\"在JavaScript中类型可以分为两类，基本类型和对象类型。JavaScript的基本类型包括数字、文本字符串、和布尔值，Null和undefined是基本值，它们也是数据类型。它们在JavaScript中有一些特殊的功能，例如作为函数的参数默认值、表示未初始化的变量等。因此，将它们视为数据类型是有意义的。最后一个是Symbol是在ES6中被引入，用作字符串属性名，Symbol是JavaScript中一种特殊的内置对象，它表示独一无二的值。Symbol值是唯一的，这使得它们成为了一种基本值。此外，Symbol值不能与其他值进行比较，也不能通过常规方法访问，因此它们在功能上与基本值类似。将Symbol视为数据类型是有意义的，因为它表示了一种不可变的、唯一的值。BigInt 是一种数字类型的数据，它于ES11被引入，成为一种基本数据类型，它可以表示任意精度格式的整数。而在其他编程语言中，可以存在不同的数字类型，例如：整数、浮点数、双精度数或大斐波数\",\"在JavaScript中对象类型就是复杂数据类型。对象是一种无序的键值对集合，可以包含任何类型的值，包括其他对象。因此，对象类型是复杂数据类型，可以用来表示和处理复杂的数据。任何不是数字、字符串、布尔值、Symbol、null 或 undefined 的 JavaScript 值都是对象。\",\"参考 JavaScript权威指南第3章 参考 基本类型 - MDN Web 文档术语表：Web 相关术语的定义 | MDN (mozilla.org)\",\"事件代理也称事件委托，是一种优化DOM操作的方法，它可以将事件处理程序从子元素移动到父元素，从而减少事件监听器的数量。 事件代理的原理是，当一个子元素上的事件触发时，父元素上的事件处理程序会检查事件是否与子元素有关。如果事件与子元素无关，那么父元素上的事件处理程序不会执行；如果事件与子元素有关，那么父元素上的事件处理程序会调用事件委托函数，将事件传递给子元素上的事件处理程序。\",\"优点：\",\"减少事件监听器的数量：\",\"事件代理可以将事件处理程序从子元素移动到父元素，从而减少事件监听器的数量。这可以提高性能，减少内存使用和页面加载时间。\",\"事件代理可以处理事件代理的子元素上的事件，而不会影响到其他子元素\",\"事件代理不会影响父元素上的其他事件处理程序，这样可以避免事件处理程序的混乱。\",\"方便事件冒泡：\",\"事件代理允许事件从父元素传递到子元素，从而实现事件冒泡。这可以提高代码的可重用性和可维护性。\",\"减少内存泄漏发生的概率\",\"JavaScript和DOM节点之间的关联变少了，这样也就减少了因循环引用而带来的内存泄漏发生的概率\",\"简单的实现\",\"<!DOCTYPE html> <html> <body> <ul id=\\\"myList\\\"> <li>Item 1</li> <li>Item 2</li> <li>Item 3</li> </ul> </body> </html> <script> const parentElement = document.getElementById('myList'); // 事件委托 parentElement.addEventListener('click', function (event) { if (event.target.nodeName === 'LI') { console.log(\\\"点击了选项：\\\", event.target.textContent) } }) </script> \"]},\"669\":{\"h\":\"Posts\"},\"670\":{\"h\":\"Java Script权威指南第7版\"},\"671\":{\"h\":\"Books\"},\"672\":{\"h\":\"Apple\"},\"673\":{\"h\":\"Banana\"},\"674\":{\"h\":\"Fast Admin\"},\"675\":{\"h\":\"框架\"}},\"dirtCount\":0,\"index\":[[\"优点\",{\"1\":{\"668\":1}}],[\"优缺点\",{\"1\":{\"668\":1}}],[\"任何不是数字\",{\"1\":{\"668\":1}}],[\"任务列表\",{\"0\":{\"19\":1}}],[\"浮点数\",{\"1\":{\"668\":1}}],[\"浮动的问题\",{\"0\":{\"179\":1}}],[\"浮动元素\",{\"1\":{\"192\":1}}],[\"浮